import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import axios from 'axios';
import PostCard from '../components/PostCard';
import './ConsumerView.css';

const API_URL = 'http://localhost:5000/api';

const ConsumerView = () => {
  const navigate = useNavigate();
  const { user, logout } = useContext(AuthContext);
  const [posts, setPosts] = useState([]);
  const [filteredPosts, setFilteredPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    fetchPosts();
  }, []);

  useEffect(() => {
    if (searchQuery.trim()) {
      const query = searchQuery.toLowerCase();
      const filtered = posts.filter(post => {
        const titleMatch = post.title?.toLowerCase().includes(query);
        const captionMatch = post.caption?.toLowerCase().includes(query);
        const locationMatch = post.location?.toLowerCase().includes(query);
        return titleMatch || captionMatch || locationMatch;
      });
      setFilteredPosts(filtered);
    } else {
      setFilteredPosts(posts);
    }
  }, [searchQuery, posts]);

  const fetchPosts = async () => {
    try {
      const response = await axios.get(`${API_URL}/posts/public`);
      setPosts(response.data);
      setFilteredPosts(response.data);
    } catch (err) {
      setError('Failed to load posts');
    } finally {
      setLoading(false);
    }
  };

  const handleLikeToggle = async (postId) => {
    if (!user || user.role !== 'consumer') {
      navigate('/consumer-login');
      return;
    }

    try {
      await axios.post(`${API_URL}/likes/${postId}`, {}, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      fetchPosts();
    } catch (err) {
      console.error('Failed to toggle like:', err);
      if (err.response?.status === 401) {
        navigate('/consumer-login');
      }
    }
  };

  const handleCommentAdded = () => {
    fetchPosts();
  };

  const handleComment = async (postId, commentText) => {
    if (!user || user.role !== 'consumer') {
      navigate('/consumer-login');
      return;
    }

    try {
      await axios.post(`${API_URL}/comments`, {
        postId,
        text: commentText
      });
      fetchPosts();
    } catch (err) {
      console.error('Failed to add comment:', err);
      if (err.response?.status === 401) {
        navigate('/consumer-login');
      }
    }
  };

  if (loading) {
    return <div className="loading">Loading posts...</div>;
  }

  return (
    <div className="consumer-view">
      <header className="consumer-header">
        <h1>igram</h1>
        <div className="header-actions">
          <div className="search-container">
            <input
              type="text"
              className="search-input"
              placeholder="Search by title, caption, or location..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
          {user && user.role === 'consumer' ? (
            <>
              <span className="user-info">{user.username}</span>
              <button className="logout-btn" onClick={() => {
                logout();
                navigate('/');
              }}>
                Logout
              </button>
            </>
          ) : user && user.role === 'creator' ? (
            <button className="creator-view-btn" onClick={() => navigate('/home')}>
              Creator View
            </button>
          ) : (
            <>
              <button className="login-btn" onClick={() => navigate('/consumer-login')}>
                Login
              </button>
              <button className="register-btn" onClick={() => navigate('/consumer-register')}>
                Register
              </button>
            </>
          )}
          <button className="creator-login-btn" onClick={() => navigate('/creator-login')}>
            Login for Creator
          </button>
        </div>
      </header>

      {error && <div className="error-message">{error}</div>}

      {!user || user.role !== 'consumer' ? (
        <div className="auth-prompt">
          <div className="auth-prompt-box">
            <h2>Welcome to igram</h2>
            <p>Please login or register to like and comment on posts</p>
            <div className="auth-prompt-actions">
              <button className="login-btn" onClick={() => navigate('/consumer-login')}>
                Login
              </button>
              <button className="register-btn" onClick={() => navigate('/consumer-register')}>
                Register
              </button>
            </div>
          </div>
        </div>
      ) : null}

      <div className="posts-container">
        {filteredPosts.length === 0 ? (
          <div className="no-posts">
            {searchQuery ? 'No posts found matching your search.' : 'No posts yet.'}
          </div>
        ) : (
          filteredPosts.map(post => (
            <PostCard
              key={post._id}
              post={post}
              currentUser={user}
              onLikeToggle={handleLikeToggle}
              onCommentAdded={handleCommentAdded}
              onComment={handleComment}
              isConsumerView={true}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default ConsumerView;
