import React, { useState } from 'react';
import axios from 'axios';
import './Modal.css';

const API_URL = 'http://localhost:5000/api';

const CreatePostModal = ({ onClose, onPostCreated }) => {
  const [title, setTitle] = useState('');
  const [caption, setCaption] = useState('');
  const [location, setLocation] = useState('');
  const [people, setPeople] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file || null);
  };

  const handlePeopleChange = (e) => {
    const value = e.target.value;
    // Only allow letters, spaces, commas, and apostrophes (for names like O'Connor)
    // Remove any numbers or special characters except commas and apostrophes
    const cleaned = value.replace(/[^a-zA-Z\s,']/g, '');
    setPeople(cleaned);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // At least caption or media must be provided
    if (!caption.trim() && !imageFile) {
      setError('Please provide either a caption or media file (or both).');
      return;
    }

    setLoading(true);

    try {
      const formData = new FormData();
      if (imageFile) {
        formData.append('image', imageFile);
      }
      formData.append('title', title);
      formData.append('caption', caption);
      formData.append('location', location);
      formData.append('people', people);

      const response = await axios.post(`${API_URL}/posts`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      onPostCreated(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create post');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Create New Post</h2>
          <button className="close-btn" onClick={onClose}>Ã—</button>
        </div>
        {error && <div className="error-message">{error}</div>}
        <form onSubmit={handleSubmit} noValidate>
          <div className="form-group">
            <label>Media File (image or video) - Optional</label>
            <input
              type="file"
              accept="image/*,video/*"
              onChange={handleFileChange}
            />
            <small style={{ color: '#666', fontSize: '12px' }}>
              You can create a text-only post by leaving this empty
            </small>
          </div>
          <div className="form-group">
            <label>Title</label>
            <input
              type="text"
              name="title"
              placeholder="Enter post title..."
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>Caption</label>
            <textarea
              name="caption"
              placeholder="Write a caption... (required if no media)"
              value={caption}
              onChange={(e) => setCaption(e.target.value)}
              rows="4"
            />
          </div>
          <div className="form-group">
            <label>Location</label>
            <input
              type="text"
              name="location"
              placeholder="Enter location..."
              value={location}
              onChange={(e) => setLocation(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>People (comma-separated names)</label>
            <input
              type="text"
              name="people"
              placeholder="e.g., John Doe, Jane Smith, Bob Johnson"
              value={people}
              onChange={handlePeopleChange}
            />
            <small style={{ color: '#666', fontSize: '12px' }}>
              Only letters, spaces, commas, and apostrophes allowed. Numbers and special characters will be removed.
            </small>
          </div>
          <div className="modal-actions">
            <button type="button" onClick={onClose} className="cancel-btn">
              Cancel
            </button>
            <button type="submit" disabled={loading} className="submit-btn">
              {loading ? 'Creating...' : 'Create Post'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreatePostModal;
