import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import axios from 'axios';
import PostCard from '../components/PostCard';
import './ConsumerView.css';

const API_URL = 'http://localhost:5000/api';

const ConsumerView = () => {
  const navigate = useNavigate();
  const { user, logout } = useContext(AuthContext);
  const [posts, setPosts] = useState([]);
  const [filteredPosts, setFilteredPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    fetchPosts();
  }, []);

  useEffect(() => {
    if (searchQuery.trim()) {
      const query = searchQuery.toLowerCase();
      const filtered = posts.filter(post => {
        const titleMatch = post.title?.toLowerCase().includes(query);
        const captionMatch = post.caption?.toLowerCase().includes(query);
        const locationMatch = post.location?.toLowerCase().includes(query);
        return titleMatch || captionMatch || locationMatch;
      });
      setFilteredPosts(filtered);
    } else {
      setFilteredPosts(posts);
    }
  }, [searchQuery, posts]);

  const fetchPosts = async () => {
    try {
      const response = await axios.get(`${API_URL}/posts/public`);
      setPosts(response.data);
      setFilteredPosts(response.data);
    } catch (err) {
      setError('Failed to load posts');
    } finally {
      setLoading(false);
    }
  };

  const handleLikeToggle = async (postId) => {
    if (!user) {
      navigate('/consumer-login');
      return;
    }
    try {
      await axios.post(`${API_URL}/likes/${postId}`);
      fetchPosts();
    } catch (err) {
      console.error('Failed to toggle like:', err);
    }
  };

  const handleCommentAdded = () => {
    fetchPosts();
  };

  const handleComment = async (postId, commentText) => {
    if (!user) {
      navigate('/consumer-login');
      return;
    }
    try {
      await axios.post(`${API_URL}/comments`, {
        postId,
        text: commentText
      });
      fetchPosts();
    } catch (err) {
      console.error('Failed to add comment:', err);
    }
  };

  if (loading) {
    return <div className="loading">Loading posts...</div>;
  }

  return (
    <div className="consumer-view">
      <header className="consumer-header">
        <h1>igram</h1>
        <div className="header-actions">
          <div className="search-container">
            <input
              type="text"
              className="search-input"
              placeholder="Search by title, caption, or location..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
          {user && user.role === 'consumer' ? (
            <>
              <span className="user-info">
                {user.username} (Consumer)
              </span>
              <button className="logout-btn" onClick={() => {
                logout();
                window.location.href = '/';
              }}>
                Sign Out
              </button>
            </>
          ) : (
            <>
              <button className="consumer-login-btn" onClick={() => navigate('/consumer-login')}>
                Sign In
              </button>
              <button className="consumer-register-btn" onClick={() => navigate('/consumer-register')}>
                Sign Up
              </button>
            </>
          )}
          <button className="creator-login-btn" onClick={() => navigate('/creator-login')}>
            Creator Login
          </button>
        </div>
      </header>

      {error && <div className="error-message">{error}</div>}

      {!user && (
        <div className="auth-prompt">
          <p>Please <button onClick={() => navigate('/consumer-login')} className="link-btn">Sign In</button> or <button onClick={() => navigate('/consumer-register')} className="link-btn">Sign Up</button> to like and comment on posts.</p>
        </div>
      )}

      <div className="posts-container">
        {filteredPosts.length === 0 ? (
          <div className="no-posts">
            {searchQuery ? 'No posts found matching your search.' : 'No posts yet.'}
          </div>
        ) : (
          filteredPosts.map(post => (
            <PostCard
              key={post._id}
              post={post}
              currentUser={user}
              onLikeToggle={handleLikeToggle}
              onCommentAdded={handleCommentAdded}
              onComment={handleComment}
              isConsumerView={true}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default ConsumerView;
