import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Modal.css';

const API_URL = 'http://localhost:5000/api';

const EditPostModal = ({ post, onClose, onPostUpdated }) => {
  const [title, setTitle] = useState(post?.title || '');
  const [caption, setCaption] = useState(post?.caption || '');
  const [location, setLocation] = useState(post?.location || '');
  const [imageFile, setImageFile] = useState(null);
  const [deleteMedia, setDeleteMedia] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Update form values when post changes
  useEffect(() => {
    if (post) {
      setTitle(post.title || '');
      setCaption(post.caption || '');
      setLocation(post.location || '');
      setImageFile(null); // Reset file input when modal opens
      setDeleteMedia(false); // Reset delete media flag
    }
  }, [post]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file || null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const formData = new FormData();
      formData.append('title', title);
      formData.append('caption', caption);
      formData.append('location', location);
      if (imageFile) {
        formData.append('image', imageFile);
      }
      if (deleteMedia) {
        formData.append('deleteMedia', 'true');
      }

      const token = localStorage.getItem('token');
      const response = await axios.put(`${API_URL}/posts/${post._id}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${token}`
        },
      });

      onPostUpdated(response.data);
      onClose();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update post');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteMedia = () => {
    if (window.confirm('Are you sure you want to delete the media? The post will become text-only.')) {
      setDeleteMedia(true);
      setImageFile(null);
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Edit Post</h2>
          <button className="close-btn" onClick={onClose}>Ã—</button>
        </div>
        {error && <div className="error-message">{error}</div>}
        <form onSubmit={handleSubmit}>
          {post.imageUrl && (
            <div className="form-group">
              <label>Current Media</label>
              <div className="current-media-preview">
                {post.mediaType === 'video' ? (
                  <video
                    src={post.imageUrl.startsWith('http') ? post.imageUrl : `http://localhost:5000${post.imageUrl}`}
                    controls
                    style={{ maxWidth: '100%', maxHeight: '200px', borderRadius: '5px' }}
                  />
                ) : (
                  <img
                    src={post.imageUrl.startsWith('http') ? post.imageUrl : `http://localhost:5000${post.imageUrl}`}
                    alt="Current media"
                    style={{ maxWidth: '100%', maxHeight: '200px', borderRadius: '5px', objectFit: 'contain' }}
                  />
                )}
                <small style={{ color: '#666', fontSize: '12px', display: 'block', marginTop: '5px' }}>
                  Current media (upload new file below to replace)
                </small>
              </div>
            </div>
          )}
          <div className="form-group">
            <label>Media File (leave empty to keep current)</label>
            <input
              type="file"
              accept="image/*,video/*"
              onChange={handleFileChange}
            />
            {imageFile && (
              <small style={{ color: '#667eea', fontSize: '12px', display: 'block', marginTop: '5px' }}>
                New file selected: {imageFile.name}
              </small>
            )}
          </div>
          <div className="form-group">
            <label>Title</label>
            <input
              type="text"
              name="title"
              placeholder="Enter post title..."
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>Caption</label>
            <textarea
              name="caption"
              placeholder="Write a caption..."
              value={caption}
              onChange={(e) => setCaption(e.target.value)}
              rows="4"
            />
          </div>
          <div className="form-group">
            <label>Location</label>
            <input
              type="text"
              name="location"
              placeholder="Enter location..."
              value={location}
              onChange={(e) => setLocation(e.target.value)}
            />
          </div>
          <div className="modal-actions">
            <button type="button" onClick={onClose} className="cancel-btn">
              Cancel
            </button>
            <button type="submit" disabled={loading} className="submit-btn">
              {loading ? 'Updating...' : 'Update Post'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditPostModal;
