import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PostCard from '../components/PostCard';
import './ConsumerView.css';

const API_URL = 'http://localhost:5000/api';

const ConsumerView = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [locationFilter, setLocationFilter] = useState('');
  const [personFilter, setPersonFilter] = useState('');

  useEffect(() => {
    fetchPosts();
  }, [searchTerm, locationFilter, personFilter]);

  const fetchPosts = async () => {
    try {
      const params = {};
      if (searchTerm) params.search = searchTerm;
      if (locationFilter) params.location = locationFilter;
      if (personFilter) params.person = personFilter;

      const response = await axios.get(`${API_URL}/posts`, { params });
      setPosts(response.data);
    } catch (err) {
      console.error('Failed to load posts:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleLikeToggle = async (postId) => {
    try {
      // For consumers, use anonymous like (stored in localStorage)
      const likedPosts = JSON.parse(localStorage.getItem('likedPosts') || '[]');
      const post = posts.find(p => p._id === postId);
      
      if (!post) return;

      const isLiked = likedPosts.includes(postId);
      let newLikedPosts;
      
      if (isLiked) {
        newLikedPosts = likedPosts.filter(id => id !== postId);
        // Unlike via API (anonymous)
        await axios.post(`${API_URL}/likes/${postId}/anonymous`, { action: 'unlike' });
      } else {
        newLikedPosts = [...likedPosts, postId];
        // Like via API (anonymous)
        await axios.post(`${API_URL}/likes/${postId}/anonymous`, { action: 'like' });
      }
      
      localStorage.setItem('likedPosts', JSON.stringify(newLikedPosts));
      fetchPosts(); // Refresh to get updated like count
    } catch (err) {
      console.error('Failed to toggle like:', err);
    }
  };

  const handleCommentAdded = () => {
    fetchPosts();
  };

  if (loading) {
    return <div className="loading">Loading posts...</div>;
  }

  return (
    <div className="consumer-view">
      <header className="consumer-header">
        <h1>Media Distribution Platform</h1>
        <div className="search-filters">
          <input
            type="text"
            placeholder="Search by title or caption..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <input
            type="text"
            placeholder="Filter by location..."
            value={locationFilter}
            onChange={(e) => setLocationFilter(e.target.value)}
            className="filter-input"
          />
          <input
            type="text"
            placeholder="Filter by person..."
            value={personFilter}
            onChange={(e) => setPersonFilter(e.target.value)}
            className="filter-input"
          />
        </div>
        <a href="/creator/login" className="creator-login-link">Creator Login</a>
      </header>

      <div className="posts-container">
        {posts.length === 0 ? (
          <div className="no-posts">No posts found. Try adjusting your search filters.</div>
        ) : (
          posts.map(post => (
            <PostCard
              key={post._id}
              post={post}
              currentUser={{ role: 'consumer', id: null }}
              onPostUpdated={() => {}}
              onPostDeleted={() => {}}
              onLikeToggle={handleLikeToggle}
              onCommentAdded={handleCommentAdded}
              isConsumerView={true}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default ConsumerView;
