import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './PostCard.css';

const API_URL = 'http://localhost:5000/api';

const PostCard = ({ post, currentUser, onPostDeleted, onLikeToggle, onCommentAdded, onComment, isConsumerView = false }) => {
  const [commentText, setCommentText] = useState('');
  const [isLiked, setIsLiked] = useState(false);
  const [likesCount, setLikesCount] = useState(0);

  useEffect(() => {
    if (isConsumerView && currentUser.name) {
      // Check consumer likes
      const liked = post.consumerLikes?.some(like => 
        like.consumerName === currentUser.name
      ) || false;
      setIsLiked(liked);
      const userLikesCount = post.likes?.length || 0;
      const consumerLikesCount = post.consumerLikes?.length || 0;
      setLikesCount(userLikesCount + consumerLikesCount);
    } else if (currentUser.id) {
      // Check authenticated user likes
      const liked = post.likes?.some(like => {
        const likeId = typeof like === 'object' && like._id ? like._id.toString() : like.toString();
        return likeId === currentUser.id;
      }) || false;
      setIsLiked(liked);
      const userLikesCount = post.likes?.length || 0;
      const consumerLikesCount = post.consumerLikes?.length || 0;
      setLikesCount(userLikesCount + consumerLikesCount);
    } else {
      setIsLiked(false);
      const userLikesCount = post.likes?.length || 0;
      const consumerLikesCount = post.consumerLikes?.length || 0;
      setLikesCount(userLikesCount + consumerLikesCount);
    }
  }, [post.likes, post.consumerLikes, currentUser.id, currentUser.name, isConsumerView]);

  const handleLike = async () => {
    try {
      await onLikeToggle(post._id);
      setIsLiked(!isLiked);
      setLikesCount(isLiked ? likesCount - 1 : likesCount + 1);
    } catch (err) {
      console.error('Failed to toggle like:', err);
    }
  };

  const handleComment = async (e) => {
    e.preventDefault();
    if (!commentText.trim()) return;

    if (isConsumerView && onComment) {
      // Consumer view - use onComment callback
      await onComment(post._id, commentText);
      setCommentText('');
    } else {
      // Authenticated user
      try {
        await axios.post(`${API_URL}/comments`, {
          postId: post._id,
          text: commentText
        });
        setCommentText('');
        onCommentAdded();
      } catch (err) {
        console.error('Failed to add comment:', err);
      }
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Are you sure you want to delete this post?')) return;

    try {
      await axios.delete(`${API_URL}/posts/${post._id}`);
      onPostDeleted(post._id);
    } catch (err) {
      console.error('Failed to delete post:', err);
      alert('Failed to delete post');
    }
  };

  const handleDeleteComment = async (commentId) => {
    if (!window.confirm('Are you sure you want to delete this comment?')) return;

    try {
      await axios.delete(`${API_URL}/comments/${commentId}`);
      onCommentAdded(); // Refresh comments
    } catch (err) {
      console.error('Failed to delete comment:', err);
      alert('Failed to delete comment');
    }
  };

  const isCreator = currentUser.role === 'creator';

  // Debug: log post data to see what we're receiving
  useEffect(() => {
    console.log('Post data:', {
      id: post._id,
      title: post.title,
      location: post.location,
      caption: post.caption,
      people: post.people
    });
  }, [post]);

  const mediaUrl = post.imageUrl
    ? (post.imageUrl.startsWith('http')
        ? post.imageUrl
        : `http://localhost:5000${post.imageUrl}`)
    : null;

  const isVideo =
    post.mediaType === 'video' ||
    (post.imageUrl &&
      /\.(mp4|webm|ogg)$/i.test(post.imageUrl));

  return (
    <div className="post-card">
      <div className="post-header">
        <div className="post-creator">
          <strong>{post.creator.username}</strong>
          {isCreator && <span className="creator-badge">Creator</span>}
        </div>
        {isCreator && (
          <div className="post-actions">
            <button className="delete-btn" onClick={handleDelete}>
              Delete
            </button>
          </div>
        )}
      </div>

      {post.imageUrl && (
        isVideo ? (
          <video
            className="post-media post-video"
            src={mediaUrl}
            controls
          />
        ) : (
          <img
            src={mediaUrl}
            alt={post.caption || 'Post'}
            className="post-media post-image"
          />
        )
      )}

      <div className="post-content">
        {post.title && post.title.trim() ? (
          <div className="post-title">
            <h3>{post.title}</h3>
          </div>
        ) : null}

        <div className="post-actions-bar">
          <button
            className={`like-btn ${isLiked ? 'liked' : ''}`}
            onClick={handleLike}
            disabled={isConsumerView && !currentUser.name}
            title={isConsumerView && !currentUser.name ? 'Please enter your name first' : ''}
          >
            {isLiked ? '‚ù§Ô∏è' : 'ü§ç'} {likesCount}
          </button>
        </div>

        {post.caption && post.caption.trim() ? (
          <div className="post-caption">
            <strong>{post.creator.username}</strong> {post.caption}
          </div>
        ) : null}

        {(post.location && post.location.trim()) || (post.people && post.people.trim()) ? (
          <div className="post-metadata">
            {post.location && post.location.trim() && (
              <span className="metadata-item">üìç {post.location}</span>
            )}
            {post.people && post.people.trim() && (
              <div className="metadata-item people-display">
                <span className="people-label">üë•</span>
                <div className="people-chips">
                  {post.people.split(',').map((name, index) => {
                    const trimmedName = name.trim();
                    return trimmedName ? (
                      <span key={index} className="people-chip-display">
                        {trimmedName}
                      </span>
                    ) : null;
                  })}
                </div>
              </div>
            )}
          </div>
        ) : null}

        <div className="post-comments">
          {post.comments && post.comments.length > 0 && (
            <div className="comments-list">
              {post.comments.map(comment => {
                const commentAuthor = comment.user?.username || comment.consumerName || 'Unknown';
                const canDelete = isCreator || (comment.user?._id === currentUser.id || comment.user === currentUser.id) || (isConsumerView && comment.consumerName === currentUser.name);
                return (
                  <div key={comment._id} className="comment">
                    <div className="comment-content">
                      <strong>{commentAuthor}</strong> {comment.text}
                    </div>
                    {canDelete && (
                      <button
                        className="delete-comment-btn"
                        onClick={() => handleDeleteComment(comment._id)}
                        title="Delete comment"
                      >
                        √ó
                      </button>
                    )}
                  </div>
                );
              })}
            </div>
          )}

          <form onSubmit={handleComment} className="comment-form">
            <input
              type="text"
              placeholder="Add a comment..."
              value={commentText}
              onChange={(e) => setCommentText(e.target.value)}
              className="comment-input"
            />
            <button type="submit" className="comment-btn">
              Post
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default PostCard;
