import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import axios from 'axios';
import PostCard from '../components/PostCard';
import './ConsumerView.css';

const API_URL = 'http://localhost:5000/api';

const ConsumerView = () => {
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);
  const [posts, setPosts] = useState([]);
  const [filteredPosts, setFilteredPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [consumerName, setConsumerName] = useState('');
  const [nameError, setNameError] = useState('');

  useEffect(() => {
    fetchPosts();
    // Check if name is stored in localStorage
    const storedName = localStorage.getItem('consumerName');
    if (storedName && storedName.trim()) {
      setConsumerName(storedName.trim());
    } else {
      // Clear any invalid name
      setConsumerName('');
      localStorage.removeItem('consumerName');
    }
  }, []);

  useEffect(() => {
    if (searchQuery.trim()) {
      const query = searchQuery.toLowerCase();
      const filtered = posts.filter(post => {
        const titleMatch = post.title?.toLowerCase().includes(query);
        const captionMatch = post.caption?.toLowerCase().includes(query);
        const locationMatch = post.location?.toLowerCase().includes(query);
        return titleMatch || captionMatch || locationMatch;
      });
      setFilteredPosts(filtered);
    } else {
      setFilteredPosts(posts);
    }
  }, [searchQuery, posts]);

  const fetchPosts = async () => {
    try {
      const response = await axios.get(`${API_URL}/posts/public`);
      setPosts(response.data);
      setFilteredPosts(response.data);
    } catch (err) {
      setError('Failed to load posts');
    } finally {
      setLoading(false);
    }
  };

  const handleNameSubmit = (nameValue) => {
    setNameError('');

    // Validate name - only alphabets and spaces
    const namePattern = /^[a-zA-Z\s]+$/;
    const nameToValidate = nameValue.trim();
    
    if (!nameToValidate) {
      setNameError('Please enter your name');
      return false;
    }
    if (!namePattern.test(nameToValidate)) {
      setNameError('Name can only contain letters and spaces');
      return false;
    }

    // Save name to localStorage
    localStorage.setItem('consumerName', nameToValidate);
    setConsumerName(nameToValidate);
    setNameError('');
    return true;
  };

  const handleNameChange = (e) => {
    let value = e.target.value;
    // Only allow letters and spaces
    value = value.replace(/[^a-zA-Z\s]/g, '');
    setConsumerName(value);
    setNameError('');
  };

  const handleLikeToggle = async (postId) => {
    if (!consumerName.trim()) {
      setNameError('Please enter your name first');
      return;
    }

    try {
      await axios.post(`${API_URL}/likes/public/${postId}`, {
        consumerName: consumerName.trim()
      });
      fetchPosts();
    } catch (err) {
      console.error('Failed to toggle like:', err);
    }
  };

  const handleCommentAdded = () => {
    fetchPosts();
  };

  const handleComment = async (postId, commentText) => {
    if (!consumerName.trim()) {
      setNameError('Please enter your name first');
      return;
    }

    try {
      await axios.post(`${API_URL}/comments/public`, {
        postId,
        text: commentText,
        consumerName: consumerName.trim()
      });
      fetchPosts();
    } catch (err) {
      console.error('Failed to add comment:', err);
    }
  };

  if (loading) {
    return <div className="loading">Loading posts...</div>;
  }

  return (
    <div className="consumer-view">
      <header className="consumer-header">
        <h1>igram</h1>
        <div className="header-actions">
          <div className="search-container">
            <input
              type="text"
              className="search-input"
              placeholder="Search by title, caption, or location..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
          {user && user.role === 'creator' ? (
            <button className="logout-btn" onClick={() => navigate('/home')}>
              Creator View
            </button>
          ) : (
            <button className="creator-login-btn" onClick={() => navigate('/creator-login')}>
              Login for Creator
            </button>
          )}
        </div>
      </header>


      {error && <div className="error-message">{error}</div>}

      <div className="posts-container">
        {filteredPosts.length === 0 ? (
          <div className="no-posts">
            {searchQuery ? 'No posts found matching your search.' : 'No posts yet.'}
          </div>
        ) : (
          filteredPosts.map(post => (
            <PostCard
              key={post._id}
              post={post}
              currentUser={{ id: consumerName || '', name: consumerName || '', role: 'consumer' }}
              onLikeToggle={handleLikeToggle}
              onCommentAdded={handleCommentAdded}
              onComment={handleComment}
              isConsumerView={true}
              consumerName={consumerName || ''}
              onNameChange={(value) => {
                setConsumerName(value);
              }}
              onNameSubmit={handleNameSubmit}
              nameError={nameError}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default ConsumerView;
