import React, { useState, useEffect } from 'react';
import axios from 'axios';
import EditPostModal from './EditPostModal';
import './PostCard.css';

const API_URL = 'http://localhost:5000/api';

const PostCard = ({ post, currentUser, onPostUpdated, onPostDeleted, onLikeToggle, onCommentAdded, isConsumerView = false }) => {
  const [showEditModal, setShowEditModal] = useState(false);
  const [commentText, setCommentText] = useState('');
  const [isLiked, setIsLiked] = useState(false);
  const [likesCount, setLikesCount] = useState(0);

  useEffect(() => {
    if (isConsumerView) {
      // For consumers, check localStorage
      const likedPosts = JSON.parse(localStorage.getItem('likedPosts') || '[]');
      setIsLiked(likedPosts.includes(post._id));
    } else {
      // For creators, check from post.likes
      const liked = post.likes.some(like => {
        const likeId = typeof like === 'object' && like._id ? like._id.toString() : like.toString();
        return likeId === currentUser.id;
      });
      setIsLiked(liked);
    }
    setLikesCount(post.likes.length);
  }, [post.likes, currentUser.id, post._id, isConsumerView]);

  const handleLike = async () => {
    try {
      await onLikeToggle(post._id);
      setIsLiked(!isLiked);
      setLikesCount(isLiked ? likesCount - 1 : likesCount + 1);
    } catch (err) {
      console.error('Failed to toggle like:', err);
    }
  };

  const handleComment = async (e) => {
    e.preventDefault();
    if (!commentText.trim()) return;

    try {
      await axios.post(`${API_URL}/comments`, {
        postId: post._id,
        text: commentText
      });
      setCommentText('');
      onCommentAdded();
    } catch (err) {
      console.error('Failed to add comment:', err);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Are you sure you want to delete this post?')) return;

    try {
      await axios.delete(`${API_URL}/posts/${post._id}`);
      onPostDeleted(post._id);
    } catch (err) {
      console.error('Failed to delete post:', err);
      alert('Failed to delete post');
    }
  };

  const handleDeleteComment = async (commentId) => {
    if (!window.confirm('Are you sure you want to delete this comment?')) return;

    try {
      await axios.delete(`${API_URL}/comments/${commentId}`);
      onCommentAdded(); // Refresh comments
    } catch (err) {
      console.error('Failed to delete comment:', err);
      alert('Failed to delete comment');
    }
  };

  const isCreator = currentUser.role === 'creator';

  const mediaUrl = post.imageUrl
    ? (post.imageUrl.startsWith('http')
        ? post.imageUrl
        : `http://localhost:5000${post.imageUrl}`)
    : null;

  const isVideo =
    post.mediaType === 'video' ||
    (post.imageUrl &&
      /\.(mp4|webm|ogg)$/i.test(post.imageUrl));

  return (
    <div className="post-card">
      <div className="post-header">
        <div className="post-creator">
          <strong>{post.creator.username}</strong>
          {isCreator && <span className="creator-badge">Creator</span>}
        </div>
        {isCreator && (
          <div className="post-actions">
            <button className="edit-btn" onClick={() => setShowEditModal(true)}>
              Edit
            </button>
            <button className="delete-btn" onClick={handleDelete}>
              Delete
            </button>
          </div>
        )}
      </div>

      {post.imageUrl && (
        isVideo ? (
          <video
            className="post-media post-video"
            src={mediaUrl}
            controls
          />
        ) : (
          <img
            src={mediaUrl}
            alt={post.caption || 'Post'}
            className="post-media post-image"
          />
        )
      )}

      <div className="post-content">
        <div className="post-actions-bar">
          <button
            className={`like-btn ${isLiked ? 'liked' : ''}`}
            onClick={handleLike}
          >
            {isLiked ? '‚ù§Ô∏è' : 'ü§ç'} {likesCount}
          </button>
        </div>

        {post.caption && (
          <div className="post-caption">
            <strong>{post.creator.username}</strong> {post.caption}
          </div>
        )}

        <div className="post-comments">
          {post.comments && post.comments.length > 0 && (
            <div className="comments-list">
              {post.comments.map(comment => {
                const canDelete = isCreator || (comment.user?._id === currentUser.id || comment.user === currentUser.id);
                return (
                  <div key={comment._id} className="comment">
                    <div className="comment-content">
                      <strong>{comment.user?.username || 'Unknown'}</strong> {comment.text}
                    </div>
                    {canDelete && (
                      <button
                        className="delete-comment-btn"
                        onClick={() => handleDeleteComment(comment._id)}
                        title="Delete comment"
                      >
                        √ó
                      </button>
                    )}
                  </div>
                );
              })}
            </div>
          )}

          <form onSubmit={handleComment} className="comment-form">
            <input
              type="text"
              placeholder="Add a comment..."
              value={commentText}
              onChange={(e) => setCommentText(e.target.value)}
              className="comment-input"
            />
            <button type="submit" className="comment-btn">
              Post
            </button>
          </form>
        </div>
      </div>

      {showEditModal && (
        <EditPostModal
          post={post}
          onClose={() => setShowEditModal(false)}
          onPostUpdated={onPostUpdated}
        />
      )}
    </div>
  );
};

export default PostCard;
